    @Test
    public void test() {
        Html html = new Html(getHtml2(), "url");
        PlainText brandName = (PlainText) html.xpath("//div[@class='jieShao']/h3/text()");
        logger.info("brandName:{}", brandName);
        PlainText title = (PlainText) html.xpath("//div[@class='titl']/h2/text()");
        logger.info("title:{}", title);
        HtmlNode bankNames = (HtmlNode) (html.xpath("//div[@class='bankNameNav']/ol/div/div").xpath("//li"));
        List<String> list = bankNames.all();
        String bankName = "";
        int index = 0;
        for (int i = 0; i < list.size(); i++) {
            String tmp = list.get(i);
            if (StringUtils.contains(tmp, "class=\"current\"")) {
                bankName = Jsoup.parse(tmp).text();
                index = i + 1;
                break;
            }
        }

        logger.info("bankName:{},index:{}", bankName, index);
        // PlainText e = (PlainText)
        // html.xpath("//li[@style='display:list-item']/text()");
        // logger.info("lables:{}", e.all());

    }

    @Test
    public void testGetBankName() {
        Elements es = Jsoup.parse(getHtml2()).select(".titl");
        String title = es.get(1).select("h2").text();
        Elements stars = es.get(1).select(".star em");
        double starsNum = stars.stream().mapToDouble(e -> {
            if (e.hasClass("one")) {
                return 1;
            }
            if (e.hasClass("half")) {
                return 0.5;
            }
            return 0;
        }).sum();

        Elements info = Jsoup.parse(getHtml2()).select(".info");
        String infos = info.get(1).select("p").text();
        logger.info("title:{},stars:{},info:{}", title, starsNum, infos);
    }

